<?php

namespace App\Service;

use GuzzleHttp\Client;
use Psr\Log\LoggerInterface;

class ImageGeneratorService
{
    private $client;
    private $apiKey;
    private $logger;

    public function __construct(string $openAiApiKey, LoggerInterface $logger)
    {
        $this->apiKey = $openAiApiKey;
        $this->logger = $logger;
        $this->client = new Client([
            'base_uri' => 'https://api.openai.com/v1/',
            'timeout' => 30.0,
        ]);
    }

    /**
     * Generate an image based on the event type.
     *
     * @param string $eventType The type of event (e.g., "Music", "Dance")
     * @return string Binary image data
     */
    public function generateImageForEventType(string $eventType): string
    {
        $this->logger->info("Generating image for event type: {$eventType}");

        // Try OpenAI DALLÂ·E API
        $imageData = $this->generateImageViaApi($eventType);

        if ($imageData) {
            $this->logger->info("Successfully generated image for event type: {$eventType}, size: " . strlen($imageData) . " bytes");
            return $imageData;
        }

        // Fallback: Use a minimal 1x1 pixel gray image (base64-encoded JPEG)
        $this->logger->warning("Using minimal base64 placeholder image for event type: {$eventType}");

        // Base64-encoded 1x1 gray pixel image (JPEG)
        $placeholderBase64 = '';

        // Decode base64 to binary
        $imageData = base64_decode($placeholderBase64);
        $this->logger->info("Generated placeholder image for event type: {$eventType}, size: " . strlen($imageData) . " bytes");
        return $imageData;
    }

    /**
     * Generate an image using the OpenAI DALLÂ·E API.
     *
     * @param string $eventType The type of event
     * @return string|null Binary image data or null on failure
     */
    private function generateImageViaApi(string $eventType): ?string
    {
        $prompts = [
            'Music' => 'A vibrant music festival scene with musical instruments and a crowd, cinematic style',
            'Dance' => 'A dynamic dance performance with colorful costumes, photorealistic',
            'Food' => 'A gourmet food spread with international cuisines, realistic lighting',
            'Art' => 'A modern art gallery with abstract paintings, professional photography',
            'Festival' => 'A lively street festival with decorations and people celebrating, vibrant colors',
            'Workshop' => 'A creative workshop with people learning and crafting, warm lighting',
        ];

        $prompt = $prompts[$eventType] ?? 'A generic event scene with people and decorations, photorealistic';

        try {
            $this->logger->info("Sending request to OpenAI API for prompt: {$prompt}");
            $response = $this->client->post('images/generations', [
                'headers' => [
                    'Authorization' => "Bearer {$this->apiKey}",
                    'Content-Type' => 'application/json',
                ],
                'json' => [
                    'prompt' => $prompt,
                    'n' => 1,
                    'size' => '1024x1024',
                    'response_format' => 'url',
                ],
            ]);

            $data = json_decode($response->getBody(), true);
            $this->logger->debug("OpenAI API response: " . json_encode($data));

            // OpenAI returns an array of data, access the first item's URL
            $imageUrl = $data['data'][0]['url'] ?? null;

            if (!$imageUrl) {
                throw new \Exception('No image URL returned from OpenAI API');
            }

            // Download the image
            $imageResponse = $this->client->get($imageUrl);
            return $imageResponse->getBody()->getContents();
        } catch (\Exception $e) {
            $this->logger->error("Failed to generate image via OpenAI API for event type: {$eventType}: " . $e->getMessage());
            return null;
        }
    }
}